static int getdigit(sxlongreal* val,int* cnt)
{
	sxlongreal d; int digit;

	if ((*cnt)++ >= 16) {
		return '0';
	}
	digit = (int)*val;
	d = digit;
	*val = (*val - d) * 10.0;
	return digit + '0';
}
