	if (nLen < SXZIP_LOCAL_HDRSZ + SXZIP_CENTRAL_HDRSZ + SXZIP_END_CENTRAL_HDRSZ) {
		return SXERR_CORRUPT;
	}

	zEnd = (unsigned char*)&zBuf[nLen - SXZIP_END_CENTRAL_HDRSZ];

