static void HashmapSortRehash(php_hashmap* pMap)
{
	php_hashmap_node* p,* pLast;
	sxu32 i;

	pLast = p = pMap->pFirst;
	pMap->iNextIdx = 0;
	i = 0;
	for (;; ) {
		if (i >= pMap->nEntry) {
			pMap->pLast = pLast;
			break;
		}
		if (p->iType == HASHMAP_BLOB_NODE) {

			SyBlobRelease(&p->xKey.sKey);

			p->iType = HASHMAP_INT_NODE;
		}
		HashmapRehashIntNode(p);

		i++;
		pLast = p;
		p = p->pPrev;
	}
}


